mapper.select:
/* BoardMapper.xml ,  selectNextPostNo */
SELECT NVL(MAX(CAST(POST_NO AS INTEGER)),0) + 1 AS POST_NO
  FROM CM_BOARD
 WHERE BOARD_ID = #{paramsMap.board_id}

mapper.update:
/* BoardMapper.xml ,  deleteBoard */
UPDATE CM_BOARD
SET DEL_YN         = 'Y',
    mod_id         = #{paramsMap.mod_id},
    mod_dt         = now()
where board_id     = #{paramsMap.board_id}
  and post_no      = #{paramsMap.post_no}
