mapper.select:
/* CmCodeGrpMapper.xml , getCmCodeGrpList */ select t.GROUP_CD , t.GROUP_NM , t.GROUP_NM_EN , t.CODE_TYPE , t.REM , t.USE_YN , t.REG_ID , t.REG_DT , t.MOD_ID , t.MOD_DT from cm_code_grp t where 1=1
AND t.GROUP_CD like concat('%',#{paramsMap.groupCd},'%')
AND t.USE_YN = #{paramsMap.useYn}
AND t.GROUP_NM = #{paramsMap.groupNm}
