mapper.select:
/* ItemMapper.xml , getItemList */ SELECT A.PLANT_CD , A.OWNER_CD , A.ITEM_CD , A.ITEM_NM , A.SPEC , A.UNIT , A.CLASS1 , A.CLASS2 , A.CLASS3 , A.BOXQTY , A.PLTQTY , A.IN_PRICE1 , A.OUT_PRICE1 , A.LOT_YN , A.FIFO_YN , A.APPRO_INV , A.IN_WH_CD , A.IN_LC_CD , A.REMARK , A.USE_YN , A.REG_ID , A.REG_DT , A.MOD_ID , A.MOD_DT FROM MST_ITEM A where 1=1
AND A.ITEM_CD like concat('%',#{paramsMap.item_cd},'%')
AND A.ITEM_NM like concat('%',#{paramsMap.item_nm},'%')
AND A.USE_YN = #{paramsMap.use_yn}
ORDER BY A.PLANT_CD, A.OWNER_CD, A.ITEM_CD

mapper.insert:
/* CustomerMapper.xml ,  insertCustomer */
insert into mst_customer
(
    plant_cd
,owner_cd
,cust_cd
,cust_nm
,cust_desc
,ceo
,business_no
,corp_no
,tel
,post_no
,addr1
,addr2
,fax
,email
,business_type
,business_item
,first_day
,remark
,use_yn
,reg_id
,reg_dt
,mod_id
,mod_dt
)
VALUES
    (
        #{paramsMap.plant_cd}
    ,#{paramsMap.owner_cd}
    ,#{paramsMap.cust_cd}
    ,#{paramsMap.cust_nm}
    ,#{paramsMap.cust_desc}
    ,#{paramsMap.ceo}
    ,#{paramsMap.business_no}
    ,#{paramsMap.corp_no}
    ,#{paramsMap.tel}
    ,#{paramsMap.post_no}
    ,#{paramsMap.addr1}
    ,#{paramsMap.addr2}
    ,#{paramsMap.fax}
    ,#{paramsMap.email}
    ,#{paramsMap.business_type}
    ,#{paramsMap.business_item}
    ,#{paramsMap.first_day}
    ,#{paramsMap.remark}
    ,#{paramsMap.use_yn}
    ,#{paramsMap.reg_id}
    ,now()
    ,#{paramsMap.mod_id}
    ,now()
    )

mapper.update:
/* CustomerMapper.xml ,  updateCustomer */
update mst_customer
set  cust_nm     = #{paramsMap.cust_nm}
  ,cust_desc   = #{paramsMap.cust_desc}
  ,ceo         = #{paramsMap.ceo}
  ,business_no = #{paramsMap.business_no}
  ,corp_no     = #{paramsMap.corp_no}
  ,tel         = #{paramsMap.tel}
  ,post_no     = #{paramsMap.post_no}
  ,addr1       = #{paramsMap.addr1}
  ,addr2       = #{paramsMap.addr2}
  ,fax         = #{paramsMap.fax}
  ,email       = #{paramsMap.email}
  ,business_type   = #{paramsMap.business_type}
  ,business_item   = #{paramsMap.business_item}
  ,first_day   = #{paramsMap.first_day}
  ,remark      = #{paramsMap.remark}
  ,use_yn      = #{paramsMap.use_yn}
  ,mod_id      = #{paramsMap.mod_id}
  ,mod_dt      = now()
where plant_cd = #{paramsMap.plant_cd}
  and owner_cd = #{paramsMap.owner_cd}
  and cust_cd  = #{paramsMap.cust_cd}

mapper.delete:
/* CustomerMapper.xml ,  deleteCustomer */
delete from mst_customer
where plant_cd = #{paramsMap.plant_cd}
  and owner_cd = #{paramsMap.owner_cd}
  and cust_cd  = #{paramsMap.cust_cd}
