mapper.select:
/* TMstLcMapper.xml , getMstLcList */ select t.plant_cd , t.lc_cd , t.lc_nm , t.wh_cd , t.zn_cd , t.lc_holdtype , t.lc_type , t.use_yn , t.remark , t.reg_id , t.reg_dt , t.mod_id , t.mod_dt from t_mst_lc t where 1=1
and t.plant_cd like concat('%',#{paramsMap.plant_cd},'%')
and t.plant_nm like concat('%',#{paramsMap.plant_nm},'%')
and t.use_yn = #{paramsMap.use_yn}
order by t.plant_cd asc

mapper.insert:
/* TMstLcMapper.xml ,  insertCmCodeGrp */
insert into t_mst_lc
(
      plant_cd
    , wh_cd
    , wh_nm
    , wh_type
    , use_yn
    , remark
    , reg_id
    , reg_dt
    , mod_id
    , mod_dt
)
values
(
      #{paramsMap.plant_cd}
    , #{paramsMap.wh_cd}
    , #{paramsMap.wh_nm}
    , #{paramsMap.wh_type}
    , #{paramsMap.use_yn}
    , #{paramsMap.remark}
    , #{paramsMap.reg_id}
    , now()
    , #{paramsMap.mod_id}
    , now()
)

mapper.update:
/* TMstLcMapper.xml ,  updateCmCodeGrp */
update t_mst_lc set


    lc_nm       = #{paramsMap.lc_nm}
  , wh_cd       = #{paramsMap.wh_cd}
  , zn_cd       = #{paramsMap.zn_cd}
  , lc_holdtype = #{paramsMap.lc_holdtype}
  , lc_type     = #{paramsMap.lc_type}
  , use_yn      = #{paramsMap.use_yn}
  , remark      = #{paramsMap.remark}
  , mod_id      = #{paramsMap.mod_id}
  , mod_dt      = now()
where plant_cd = #{paramsMap.plant_cd}
  and lc_cd    = #{paramsMap.lc_cd}

mapper.delete:
/* TMstLcMapper.xml ,  deleteMstLc */
delete
from t_mst_lc
where plant_cd = #{paramsMap.plant_cd}
  and lc_cd    = #{paramsMap.lc_cd}
