mapper.select:
/* CmCodeGrpMapper.xml , getCmCodeGrpList */ select t.plant_cd t.plant_nm t.addr t.use_yn t.remark t.reg_id t.reg_dt t.mod_id t.mod_dt from t_mst_plant t where 1=1
and t.plant_cd like concat('%',#{paramsMap.plant_cd},'%')
and t.plant_nm = #{paramsMap.plant_nm}
and t.use_yn = #{paramsMap.groupNm}
order by t.plant_cd asc
