mapper.select:
/* TMstPlantMapper.xml , getMstPlantList */ select t.plant_cd , t.plant_nm , t.addr , t.use_yn , t.remark , t.reg_id , t.reg_dt , t.mod_id , t.mod_dt from t_mst_plant t where 1=1
and t.plant_cd like concat('%',#{paramsMap.plant_cd},'%')
and t.plant_nm = #{paramsMap.plant_nm}
and t.use_yn = #{paramsMap.use_yn}
order by t.plant_cd asc

mapper.insert:
/* TMstPlantMapper.xml ,  insertCmCodeGrp */
insert into t_mst_plant
(
      plant_cd
    , plant_nm
    , addr
    , use_yn
    , remark
    , reg_id
    , reg_dt
    , mod_id
    , mod_dt
)
values
(
     #{paramsMap.plant_cd}
   , #{paramsMap.plant_nm}
   , #{paramsMap.addr}
   , #{paramsMap.use_yn}
   , #{paramsMap.remark}
   , #{paramsMap.reg_id}
   , now()
   , #{paramsMap.mod_id}
   , now()
)

mapper.update:
/* TMstPlantMapper.xml ,  updateCmCodeGrp */
update t_mst_plant set
    plant_nm = #{paramsMap.plant_nm}
  , addr     = #{paramsMap.addr}
  , use_yn   = #{paramsMap.use_yn}
  , remark   = #{paramsMap.remark}
  , mod_id   = #{paramsMap.mod_id}
  , mod_dt   = now()
where plant_cd = #{paramsMap.plant_cd}

mapper.delete:
/* TMstPlantMapper.xml ,  deleteMstPlant */
delete
from t_mst_plant
where plant_cd = #{paramsMap.plant_cd}
